//Print sum of all even numbers b/w 1 to n
package neww;
import java.util.*;

public class P17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Enter a number");
		Scanner sc= new Scanner(System.in); 
		int m= sc.nextInt();
		System.out.println(m*(m+1));
	}

}
