//Program to print multiplication tables from 1 to 10 upto 12.
package neww;

public class P43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p;
		for (int m=1;m<=10;m++) {
		for (int i=1;i<=12;i++) {
			p=m*i;
			System.out.println(m+"*"+i+"="+p);
		}
	}
	}
}
